sys_ui_action.script:
syncEvents();

function syncEvents(){
	var r = new sn_ws.RESTMessageV2('x_snc_dev_meet.Meetup.com API', 'GET Events');
	r.setStringParameterNoEscape('urlname', gs.getProperty('x_snc_dev_meet.urlname'));
	r.setStringParameterNoEscape('key', gs.getProperty('x_snc_dev_meet.key'));
	var response = r.execute();
	var responseBody = response.getBody();
	var httpStatus = response.getStatusCode();

	if(httpStatus!=200){
		gs.addErrorMessage(responseBody.error.message);
		gs.addErrorMessage(responseBody.error.detail);

	} else {
		responseBody = JSON.parse(responseBody);
		responseBody.forEach(function(body){
			var meeting = new GlideRecord('x_snc_dev_meet_meetup');
			var find = meeting.get('id',body.id);
			meeting.name=body.group.urlname;
			meeting.id=body.id;
			meeting.date=body.local_date;
			meeting.description=body.description;
			meeting.link=body.link;
			meeting.status=body.status;
			if (find){
				meeting.update();
			} else {
				meeting.insert();
			}
		});
		gs.addInfoMessage("Meetings Synced");
	}
}
